import React from 'react';
import { motion } from 'framer-motion';
import { Scene } from '../../types';
import { 
  Users, 
  MapPin, 
  Heart, 
  Activity, 
  BookOpen,
  Palette,
  Eye
} from 'lucide-react';

interface SceneViewerProps {
  scenes: Scene[];
  title?: string;
}

const SceneViewer: React.FC<SceneViewerProps> = ({ scenes, title = "Scene Estratte" }) => {
  if (!scenes || scenes.length === 0) {
    return (
      <div className="bg-gray-50 rounded-lg p-6 text-center">
        <BookOpen className="w-8 h-8 text-gray-400 mx-auto mb-2" />
        <p className="text-gray-600">Nessuna scena disponibile</p>
      </div>
    );
  }

  return (
    <div className="space-y-4">
      <div className="flex items-center space-x-2 mb-4">
        <Eye className="w-5 h-5 text-primary-600" />
        <h3 className="text-lg font-semibold text-gray-900">{title}</h3>
        <span className="bg-primary-100 text-primary-700 px-2 py-1 rounded-full text-sm font-medium">
          {scenes.length} scene{scenes.length !== 1 ? '' : 'a'}
        </span>
      </div>

      <div className="grid gap-4">
        {scenes.map((scene, index) => (
          <motion.div
            key={index}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: index * 0.1 }}
            className="bg-white border border-gray-200 rounded-lg p-4 shadow-sm hover:shadow-md transition-shadow"
          >
            <div className="flex items-center justify-between mb-3">
              <h4 className="font-medium text-gray-900">Scena {index + 1}</h4>
              <div className="flex items-center space-x-1">
                <Palette className="w-4 h-4 text-gray-400" />
                <span className="text-sm text-gray-500 capitalize">
                  {scene.mood_vibe.split(',')[0].trim()}
                </span>
              </div>
            </div>

            <div className="grid md:grid-cols-2 gap-4">
              {/* Colonna sinistra */}
              <div className="space-y-3">
                <div>
                  <div className="flex items-center space-x-2 mb-1">
                    <Users className="w-4 h-4 text-blue-500" />
                    <span className="text-sm font-medium text-gray-700">Personaggi</span>
                  </div>
                  <p className="text-sm text-gray-600 leading-relaxed">
                    {scene.personaggi}
                  </p>
                </div>

                <div>
                  <div className="flex items-center space-x-2 mb-1">
                    <MapPin className="w-4 h-4 text-green-500" />
                    <span className="text-sm font-medium text-gray-700">Ambientazione</span>
                  </div>
                  <p className="text-sm text-gray-600 leading-relaxed">
                    {scene.ambientazione}
                  </p>
                </div>
              </div>

              {/* Colonna destra */}
              <div className="space-y-3">
                <div>
                  <div className="flex items-center space-x-2 mb-1">
                    <Activity className="w-4 h-4 text-purple-500" />
                    <span className="text-sm font-medium text-gray-700">Azione</span>
                  </div>
                  <p className="text-sm text-gray-600 leading-relaxed">
                    {scene.azione_in_corso}
                  </p>
                </div>

                <div>
                  <div className="flex items-center space-x-2 mb-1">
                    <Heart className="w-4 h-4 text-red-500" />
                    <span className="text-sm font-medium text-gray-700">Mood/Atmosfera</span>
                  </div>
                  <div className="flex flex-wrap gap-1">
                    {scene.mood_vibe.split(',').map((mood, i) => (
                      <span 
                        key={i}
                        className="bg-red-50 text-red-700 px-2 py-1 rounded text-xs font-medium"
                      >
                        {mood.trim()}
                      </span>
                    ))}
                  </div>
                </div>
              </div>
            </div>

            {/* Elementi narrativi */}
            {scene.elementi_narrativi && (
              <div className="mt-3 pt-3 border-t border-gray-100">
                <div className="flex items-center space-x-2 mb-1">
                  <BookOpen className="w-4 h-4 text-orange-500" />
                  <span className="text-sm font-medium text-gray-700">Elementi Narrativi</span>
                </div>
                <p className="text-sm text-gray-600 leading-relaxed">
                  {scene.elementi_narrativi}
                </p>
              </div>
            )}
          </motion.div>
        ))}
      </div>
    </div>
  );
};

export default SceneViewer;
